function BTSP_undisplayElement(id) {
    $(id).style.display = "none"
}

function BTSP_displayElement(id) {
    $(id).style.display = "block"
}

function BTSP_hideElement(id) {
    $(id).style.visible = false
}

function BTSP_showElement(id) {
    $(id).style.visible = true
}